parameters.jsCode:
// Process merged data from Circle Gateway and The Graph
const items = $input.all();

let balanceData = {};
let priceData = {};

// Identify which response is which by structure
for (const item of items) {
  const json = item.json || {};
  if (json.data?.bundles) {
    priceData = json;
  } else if (json.balances || json.data?.balances || json.success === false) {
    balanceData = json;
  }
}

// Parse Circle balance
let totalUsdc = 0;
const chainBalances = [];
const domainNames = {
  0: 'Ethereum',
  3: 'Arbitrum', 
  6: 'Base'
};

const balances = balanceData.balances || balanceData.data?.balances || [];
for (const balance of balances) {
  const amount = parseFloat(balance.amount || '0') / 1e6;
  totalUsdc += amount;
  chainBalances.push({
    chain: domainNames[balance.domain] || `Domain ${balance.domain}`,
    amount: amount,
  });
}

// Parse ETH price from The Graph bundles
let ethPrice = 0;
if (priceData.data?.bundles?.[0]) {
  ethPrice = parseFloat(priceData.data.bundles[0].ethPriceUSD) || 0;
}

// Format for display
const formatCurrency = (n) => `$${n.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

const chainList = chainBalances.length > 0
  ? chainBalances.map(c => `  - ${c.chain}: ${formatCurrency(c.amount)}`).join('\n')
  : '  No balances found';

return [{
  json: {
    title: 'Daily Treasury Report',
    date: new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }),
    totalBalance: formatCurrency(totalUsdc),
    totalBalanceRaw: totalUsdc,
    ethPrice: formatCurrency(ethPrice),
    ethPriceRaw: ethPrice,
    chainBreakdown: chainList,
    chainBalances,
    timestamp: new Date().toISOString(),
  }
}];

