// Save daily balance snapshot to Appwrite
const items = $input.all();
const data = items[0]?.json || {};

const endpoint = $env.APPWRITE_ENDPOINT;
const projectId = $env.APPWRITE_PROJECT_ID;
const apiKey = $env.APPWRITE_API_KEY;
const databaseId = $env.APPWRITE_DATABASE_ID || 'treasury';

if (!endpoint || !apiKey) {
  return [{ json: { saved: false, reason: 'Appwrite not configured', data: data } }];
}

try {
  const response = await fetch(
    `${endpoint}/databases/${databaseId}/collections/balances/documents`,
    {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-Appwrite-Project': projectId,
        'X-Appwrite-Key': apiKey,
      },
      body: JSON.stringify({
        documentId: 'unique()',
        data: {
          timestamp: data.timestamp || new Date().toISOString(),
          total_usd: String(data.totalBalanceRaw || 0),
          chain_balances: JSON.stringify(data.chainBalances || []),
          eth_price: String(data.ethPriceRaw || 0),
          source: 'daily_report',
        },
      }),
    }
  );
  const result = await response.json();
  return [{ json: { saved: true, documentId: result.$id, data: data } }];
} catch (error) {
  return [{ json: { saved: false, reason: error.message, data: data } }];
}
