// Read Gateway balances on-chain via RPC (instant, no Circle API lag)
const { ethers } = require('ethers');

const GATEWAY_WALLET = '0x77777777Dcc4d5A8B6E418Fd04D8997ef11000eE';
const USDC = {
  1: '0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48',
  42161: '0xaf88d065e77c8cC2239327C5EDb3A432268e5831',
  8453: '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913',
};
const CHAINS = [
  { chainId: 1, domain: 0, rpc: 'https://eth.llamarpc.com' },
  { chainId: 42161, domain: 3, rpc: $env.RPC_ARBITRUM || 'https://arb1.arbitrum.io/rpc' },
  { chainId: 8453, domain: 6, rpc: $env.RPC_BASE || 'https://mainnet.base.org' },
];

const abi = ['function totalBalance(address token, address depositor) view returns (uint256)'];
const depositor = $env.TREASURY_ADDRESS;

const balances = [];
for (const chain of CHAINS) {
  try {
    const provider = new ethers.JsonRpcProvider(chain.rpc);
    const contract = new ethers.Contract(GATEWAY_WALLET, abi, provider);
    const balance = await contract.totalBalance(USDC[chain.chainId], depositor);
    balances.push({ domain: chain.domain, balance: ethers.formatUnits(balance, 6) });
  } catch (e) {
    balances.push({ domain: chain.domain, balance: '0' });
  }
}

return [{ json: { balances } }];

// Process merged data from Circle Gateway and The Graph
const items = $input.all();

let balanceData = {};
let priceData = {};

// Identify which response is which by structure
for (const item of items) {
  const json = item.json || {};
  if (json.data?.bundles) {
    priceData = json;
  } else if (json.token === 'USDC' || json.balances || json.data?.balances) {
    balanceData = json;
  }
}

// Parse Circle Gateway balance (new format: balance is string like "1000.50")
let totalUsdc = 0;
const chainBalances = [];
const domainNames = {
  0: 'Ethereum',
  3: 'Arbitrum', 
  6: 'Base'
};

const balances = balanceData.balances || balanceData.data?.balances || [];
for (const bal of balances) {
  // New API returns 'balance' as formatted string (e.g., "1000.50")
  const amount = parseFloat(bal.balance || bal.amount || '0');
  totalUsdc += amount;
  chainBalances.push({
    chain: domainNames[bal.domain] || `Domain ${bal.domain}`,
    amount: amount,
  });
}

// Parse ETH price from The Graph bundles
let ethPrice = 0;
if (priceData.data?.bundles?.[0]) {
  ethPrice = parseFloat(priceData.data.bundles[0].ethPriceUSD) || 0;
}

// Format for display
const formatCurrency = (n) => `$${n.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

const chainList = chainBalances.length > 0
  ? chainBalances.map(c => `  - ${c.chain}: ${formatCurrency(c.amount)}`).join('\\n')
  : '  No balances found (deposit USDC to Gateway first)';

return [{
  json: {
    title: 'Daily Treasury Report',
    date: new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' }),
    totalBalance: formatCurrency(totalUsdc),
    totalBalanceRaw: totalUsdc,
    ethPrice: formatCurrency(ethPrice),
    ethPriceRaw: ethPrice,
    chainBreakdown: chainList,
    chainBalances,
    timestamp: new Date().toISOString(),
  }
}];

