const content = $input.first().json.body?.content || $input.first().json.content || '';
const lowerContent = content.toLowerCase().trim();
let intent = 'unknown';
let params = {};

// Check for deposit intent first (new!)
if (lowerContent.includes('deposit')) {
  intent = 'deposit';
  // Match patterns like "deposit $100", "deposit 50 USDC", "deposit $100 on Base"
  const depositMatch = lowerContent.match(/deposit\s*\$?(\d+(?:\.\d+)?)\s*(?:usdc)?(?:\s+(?:on|to)\s+(ethereum|arbitrum|base))?/i);
  if (depositMatch) {
    params.amount = depositMatch[1];
    params.chain = depositMatch[2] || 'arbitrum'; // default to Arbitrum
  }
} else if (lowerContent.includes('swap') || lowerContent.includes('convert') || lowerContent.includes('exchange')) {
  intent = 'swap';
  const swapMatch = lowerContent.match(/(\d+(?:\.\d+)?)\s*(usdc|eth|usdt)\s*(?:to|for|into)\s*(usdc|eth|usdt)/i);
  if (swapMatch) {
    params.amount = swapMatch[1];
    params.fromToken = swapMatch[2].toUpperCase();
    params.toToken = swapMatch[3].toUpperCase();
  }
} else if (lowerContent.includes('send') || lowerContent.includes('transfer')) {
  intent = 'send';
  const sendMatch = lowerContent.match(/(\d+(?:\.\d+)?)\s*(usdc|eth)\s*(?:to)\s*([\w\.]+)/i);
  if (sendMatch) {
    params.amount = sendMatch[1];
    params.token = sendMatch[2].toUpperCase();
    params.recipient = sendMatch[3];
  }
} else if (lowerContent.includes('balance') || lowerContent.includes('how much')) {
  intent = 'balance';
} else if (lowerContent.includes('price') || lowerContent.match(/^(eth|bitcoin|btc)\s*\??$/)) {
  intent = 'price';
  params.token = lowerContent.includes('btc') || lowerContent.includes('bitcoin') ? 'BTC' : 'ETH';
} else if (lowerContent.includes('help') || lowerContent === '?') {
  intent = 'help';
} else if (lowerContent.match(/^(hi|hello|hey|gm|good morning)/)) {
  intent = 'greeting';
}

return [{json: {...$input.first().json.body || $input.first().json, intent, params, originalContent: content}}];

const data = $input.first().json;
const author = $node['Parse Intent'].json.author?.displayName || 'there';

// Check for errors
if (data.error || (data.message && !data.balances)) {
  return [{json: {reply: `Hey ${author}! Error fetching balance: ${data.message || data.error}`}}];
}

// Parse Circle Gateway response (new format: balance is string like "1000.50")
const balances = data.balances || [];
let totalUsdc = 0;
const chainBalances = [];
const domainNames = {0: 'Ethereum', 3: 'Arbitrum', 6: 'Base'};

for (const bal of balances) {
  // New API returns 'balance' as formatted string (e.g., "1000.50")
  const amount = parseFloat(bal.balance || bal.amount || '0');
  totalUsdc += amount;
  if (amount > 0) {
    chainBalances.push(`${domainNames[bal.domain] || 'Chain ' + bal.domain}: $${amount.toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2})}`);
  }
}

const balanceList = chainBalances.length > 0 
  ? chainBalances.join('\n- ') 
  : 'No USDC balances found (deposit USDC to Gateway first)';

const totalFormatted = totalUsdc.toLocaleString('en-US', {minimumFractionDigits: 2, maximumFractionDigits: 2});

return [{json: {reply: `Hey ${author}! Here's your treasury balance:\n\n**Total USDC: $${totalFormatted}**\n- ${balanceList}\n\n_Powered by Circle Gateway_`}}];

const data = $input.first().json;
const ethPrice = parseFloat(data?.data?.bundles?.[0]?.ethPriceUSD || 0);
if (ethPrice === 0) {
  return [{json: {reply: 'Error fetching price'}}];
}
const formatted = ethPrice.toLocaleString('en-US', {style: 'currency', currency: 'USD', minimumFractionDigits: 2, maximumFractionDigits: 2});
return [{json: {reply: `**ETH Price**: ${formatted}\n\n_Data from Uniswap v3 on Arbitrum via The Graph_`}}];

const params = $input.first().json.params || {};
if (!params.amount || !params.fromToken || !params.toToken) {
  return [{json: {reply: `I couldn't parse your swap request. Try: "swap 100 USDC to ETH"`}}];
}
return [{json: {reply: `**Swap Quote**

From: ${params.amount} ${params.fromToken}
To: ~${(params.amount / 3450).toFixed(4)} ${params.toToken}

Route: Uniswap v4 (same-chain)
Estimated fee: ~$0.50

_Reply "confirm" to execute or "cancel" to abort_`}}];

const params = $input.first().json.params || {};
const author = $input.first().json.author?.displayName || 'there';
const executionMode = $env.EXECUTION_MODE || 'mock';

if (!params.amount) {
  return [{json: {reply: `I couldn't parse your deposit request. Try: "deposit $100" or "deposit 50 on Base"`}}];
}

// Map chain names to chain IDs
const chainIds = {
  'ethereum': 1,
  'arbitrum': 42161,
  'base': 8453
};
const chainNames = {
  1: 'Ethereum',
  42161: 'Arbitrum',
  8453: 'Base'
};

const chainName = (params.chain || 'arbitrum').toLowerCase();
const chainId = chainIds[chainName] || 42161;
const displayChain = chainNames[chainId];

if (executionMode === 'mock') {
  // Mock mode - return simulated response
  const mockTxHash = '0xmock_' + Date.now().toString(16) + '_deposit';
  return [{
    json: {
      reply: `**Deposit Simulated (Mock Mode)**\n\nAmount: $${params.amount} USDC\nChain: ${displayChain}\nTx Hash: \`${mockTxHash}\`\n\n_Switch to live mode for real deposits. Use the dashboard for wallet-based deposits._`
    }
  }];
}

// Live mode - would call Appwrite function here
// For now, return instructions to use dashboard
return [{
  json: {
    reply: `**Deposit Request**\n\nAmount: $${params.amount} USDC\nChain: ${displayChain}\n\nTo deposit from your personal wallet, use the **Treasury Dashboard**:\nhttps://treasury-agent.sites.smartpiggies.cloud\n\n_Dashboard deposits are signed with your wallet for security._`
  }
}];

const content = $input.first().json.originalContent || '';
const intent = $input.first().json.intent;
if (intent === 'greeting') {
  return [{json: {reply: `Hey! I'm Treasury Agent. I can help you check balances, prices, execute swaps, and deposit USDC. Try "balance" or "help" to get started!`}}];
}
return [{json: {reply: `I'm not sure what you mean by "${content.substring(0, 50)}"...\n\nTry "help" to see what I can do!`}}];
