// Fetch Gateway balances from Circle REST API (accurate available balance)
const fetch = require('node-fetch');
const CIRCLE_GATEWAY_URL = $env.CIRCLE_GATEWAY_URL || 'https://gateway-api.circle.com';
const depositor = $env.TREASURY_ADDRESS;

const sources = [
  { domain: 0, depositor },
  { domain: 3, depositor },
  { domain: 6, depositor },
];

const response = await fetch(`${CIRCLE_GATEWAY_URL}/v1/balances`, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ token: 'USDC', sources }),
});

if (!response.ok) {
  const body = await response.text();
  throw new Error(`Circle API error ${response.status}: ${body}`);
}

const data = await response.json();
return [{ json: { balances: data.balances, token: data.token } }];

// Fetch on-chain wallet balances (USDC + ETH) on each chain via JSON-RPC
const fetch = require('node-fetch');
const treasury = $env.TREASURY_ADDRESS;

const chains = [
  { name: 'Ethereum', rpc: $env.RPC_ETHEREUM || 'https://eth.llamarpc.com', usdc: '0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48' },
  { name: 'Arbitrum', rpc: $env.RPC_ARBITRUM || 'https://arb1.arbitrum.io/rpc', usdc: '0xaf88d065e77c8cC2239327C5EDb3A432268e5831' },
  { name: 'Base', rpc: $env.RPC_BASE || 'https://mainnet.base.org', usdc: '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913' },
];

// ERC20 balanceOf(address) selector = 0x70a08231 + address padded to 32 bytes
const balanceOfData = '0x70a08231' + treasury.slice(2).toLowerCase().padStart(64, '0');

async function rpcCall(rpc, method, params) {
  const res = await fetch(rpc, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ jsonrpc: '2.0', id: 1, method, params }),
  });
  const data = await res.json();
  return data.result || '0x0';
}

const results = await Promise.all(chains.map(async (chain) => {
  try {
    const [ethHex, usdcHex] = await Promise.all([
      rpcCall(chain.rpc, 'eth_getBalance', [treasury, 'latest']),
      rpcCall(chain.rpc, 'eth_call', [{ to: chain.usdc, data: balanceOfData }, 'latest']),
    ]);
    const ethBalance = parseInt(ethHex, 16) / 1e18;
    const usdcBalance = parseInt(usdcHex, 16) / 1e6;
    return { chain: chain.name, ethBalance, usdcBalance };
  } catch (err) {
    return { chain: chain.name, ethBalance: 0, usdcBalance: 0 };
  }
}));

return [{ json: { _type: 'wallet_balances', wallet_balances: results } }];

// Process merged data from Circle Gateway, The Graph, and wallet RPC
const items = $input.all();

let balanceData = {};
let priceData = {};
let walletData = [];

for (const item of items) {
  const json = item.json || {};
  if (json._type === 'wallet_balances') {
    walletData = json.wallet_balances || [];
  } else if (json.data?.bundles) {
    priceData = json;
  } else if (json.token === 'USDC' || json.balances || json.data?.balances) {
    balanceData = json;
  }
}

// Circle Gateway balance â€” unified, chain-abstracted (one total)
let gateway_usd = 0;
const balances = balanceData.balances || balanceData.data?.balances || [];
for (const bal of balances) {
  gateway_usd += parseFloat(bal.balance || bal.amount || '0');
}

// ETH price from The Graph
let eth_price = 0;
if (priceData.data?.bundles?.[0]) {
  eth_price = parseFloat(priceData.data.bundles[0].ethPriceUSD) || 0;
}

// Wallet balances per chain
let wallet_usdc = 0;
let wallet_eth_usd = 0;
const wallet_chains = walletData.map(w => {
  wallet_usdc += w.usdcBalance || 0;
  wallet_eth_usd += (w.ethBalance || 0) * eth_price;
  return {
    chain: w.chain,
    usdc: w.usdcBalance || 0,
    eth: w.ethBalance || 0,
    eth_usd: (w.ethBalance || 0) * eth_price,
  };
});

return [{
  json: {
    total_usd: gateway_usd + wallet_usdc + wallet_eth_usd,
    gateway_usd,
    wallet_usdc,
    wallet_eth_usd,
    wallet_chains,
    eth_price,
    timestamp: new Date().toISOString(),
  }
}];
